,p_process_name=>'Copiar Formula'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'',
'-- variables para cursor que rescata formula a copiar',
'v_id_consulta_nutricion number;',
'v_tomas number;',
'v_cantidad number;',
'v_volumen_total number;',
'v_ml_toma number;',
'v_horario VARCHAR(200);',
'v_tipo VARCHAR(50);',
'v_osmo_total number;',
'',
'-- variables para cursor que rescata el detalle de la formula a copiar',
'v_id_formula number;',
'v_medidas number;',
'v_formula_especial VARCHAR(50);',
'v_cantidad2 NUMBER;',
'v_osmo number;',
'',
'-- variable para guardar el nuevo ID de la nueva formula de la region Indicacion',
'v_id_nueva_formula NUMBER;',
'',
'-- cursor para rescatar info de formula a copiar',
'cursor formulas IS',
'select ID_CONSULTA_NUTRICION, TOMAS, CANTIDAD, VOLUMEN_TOTAL, ML_TOMA, HORARIO, TIPO, OSMO_TOTAL from INTA_FORMULAS',
'WHERE ID_CONSULTA_NUTRICION = :P16_ID AND TIPO = ''Ingesta'';',
'',
'-- cursor para rescatar info del detalle de la formula a copiar',
'cursor formulas_detalle IS',
'select ID_FORMULA, MEDIDAS, FORMULA_ESPECIAL, CANTIDAD, OSMO from INTA_FORMULAS_DETALLE',
'WHERE ID_FORMULA = (select id from inta_formulas where id_consulta_nutricion =  :P16_id and tipo = ''Ingesta'');',
'',
'begin',
'		open formulas;',
'				LOOP',
'					-- copiando los datos en variables e insertando las variables en la tabla INTA_FORMULAS para la region Indicacion',
'					fetch formulas into v_id_consulta_nutricion, v_tomas, v_cantidad, v_volumen_total, v_ml_toma, v_horario, v_tipo, v_osmo_total;',
'					exit when formulas%NOTFOUND;',
'						if(formulas%ROWCOUNT = 1) then',
'							insert into inta_formulas values (null, :P16_ID, v_tomas, v_cantidad, v_volumen_total, v_ml_toma, v_horario, ''Indicacion'', v_osmo_total);				',
'						end if;',
'				end loop;',
'		close formulas;',
'		',
'		-- rescatando el nuevo ID',
'		SELECT ID INTO v_id_nueva_formula FROM INTA_FORMULAS WHERE ID_CONSULTA_NUTRICION = :P16_ID and TIPO = ''Indicacion'';',
'		',
'		open formulas_detalle;',
'			LOOP',
'				-- copiando los datos en variables e insertando las variables en la tabla INTA_FORMULAS_DETALLE para la region Indicacion',
'				fetch formulas_detalle into v_id_consulta_nutricion, v_medidas, v_formula_especial, v_cantidad2, v_osmo;',
'				exit when formulas_detalle%NOTFOUND;				',
'					insert into inta_formulas_detalle values (null, v_id_nueva_formula, v_medidas, v_formula_especial, v_cantidad2, v_osmo);',
'			end loop;',
'		close formulas_detalle;',
'	EXCEPTION WHEN NO_DATA_FOUND THEN DBMS_OUTPUT.PUT_LINE ('''');',
'	',
'END;'))
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_api.id(52560513119921185054)
